import com.eviware.soapui.model.testsuite.TestRunner.Status
import groovy.json.JsonSlurper 
import java.util.Date;
import java.io.File;
import java.util.*;
import java.lang.*; 
import jxl.*;
import jxl.write.*
import com.soapui.BuildJsonPath.*
import soapui.com.jayway.jsonpath.*
import static org.junit.Assert.*;


inputFileName = "D:\\PRE\\run\\System_Test_Cases_Groovy_Input.xls"
//inputFileName = "D:\\PRE\\run\\System_Test_Cases_Groovy_Input.xls"
//Key in the json path to be ignored. for mutiple path, give it comma seperated.
ignoreJsonPathValidation="transactionIdentifier.transactionId,transactionId"
class Driver
{
	def inputFileName
	def outputFileName
	def ignoreJsonPathValidation
	def static assertionList = []

	def Driver(def inputFileName,def outputFileName,def ignoreJsonPathValidation)
	{
		this.inputFileName=inputFileName
		this.outputFileName=outputFileName
		this.ignoreJsonPathValidation=ignoreJsonPathValidation
	}

	def run(def log,def testRunner,def context)
	{
		def wb = Workbook.getWorkbook(new File(inputFileName))
		WritableWorkbook workbook = Workbook.createWorkbook(new File(outputFileName),wb)
		def sheet1 = wb.getSheet(0)
		def sheet_r = workbook.getSheet(0)
		sheet_r.addCell(new Label(5,0,"Result"))
		sheet_r.addCell(new Label(6,0,"Exception"))
		//Create Validation Sheet
		WritableSheet sheet_v = workbook.createSheet("JSON_Validation",1)
		sheet_v.addCell(new Label(0,0,"Test Step"))
		sheet_v.addCell(new Label(1,0,"JSON Path"))
		sheet_v.addCell(new Label(2,0,"Expected Result"))
		sheet_v.addCell(new Label(3,0,"Actual Result"))
		sheet_v.addCell(new Label(4,0,"Result"))
		sheet_v.addCell(new Label(5,0,"Exception"))
		def row_count = sheet1.getRows()
		def inputPath = new File(inputFileName).getParent().toString()
		def validation_row_num=1
		for(def i=1; i< row_count; i++)
		{
			def test_suite = sheet1.getCell(0,i).getContents()
			if(test_suite.isEmpty()){continue}
			def test_case = sheet1.getCell(1,i).getContents()
			def test_step = sheet1.getCell(2,i).getContents()
			def validationCsvFilePath = "${inputPath}\\${sheet1.getCell(3,i).getContents()}"
			def run_state = sheet1.getCell(4,i).getContents()
			def result = "Pass"
			if (run_state.equalsIgnoreCase("FALSE"))
			{
				continue
			}

			def t_suite = testRunner.testCase.testSuite.project.testSuites[test_suite]
			if (t_suite==null)
			{
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} -> Test Suite is not present"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,"Test Suite is not present"))
				continue	
			}

			def tc = t_suite .testCases[test_case]

			if(tc==null)
			{
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} -> Test Case is not present"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,"Test Case is not present"))
				continue	
			}

			def ts = tc.testSteps[test_step]

			if(ts==null)
			{
			log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} -> Test Step is not present"
			sheet_r.addCell(new Label(5,i,"Error"))
			sheet_r.addCell(new Label(6,i,"Test Step is not present"))
			continue	
			}

			def response
			try{
				def runner = ts.run(testRunner, context)
				response= runner.response.contentAsString
				//log.info "Response is ${response}"
			}
			catch(Exception expObj) {
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} has Exception ${expObj}"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,expObj.toString()))
				continue
			}			

			log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} Request is run and JSON Validation in progress"

			if(!new File(validationCsvFilePath).exists())
			{
				log.info "Skipping the validation and validation JSON File: ${new File(validationCsvFilePath).getName()} does not exists."
				continue
			}


			//Parsing JSON response using JsonParse class imported from json-path.jar file
			def jsonParse
			try{
			jsonParse = new JsonParse(response)
			}
			catch(Exception expObj)
			{
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} has Exception ${expObj}"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,expObj.toString()))
				continue	
			}

			//Looping through json path and expected result from csv file
			context.fileReader = new BufferedReader(new FileReader(validationCsvFilePath))
			def rowsData = context.fileReader.readLines()
			def rowsize = rowsData.size()
			jsonpath:
		     for(def j=1; j< rowsize;  j++)
    			{
    				def rowdata = rowsData[j]
    				//Splitting the string to 2 based on comma delimiter overcoming csv restriction
    				String[] data = rowdata.split(",",2)
    				data[0]=data[0].replaceAll("=","")
    				data[0]=data[0].replaceAll("\"","")
    				data[1]=data[1].replaceAll("=","")
    				data[1]=data[1].replaceAll("\"","")
    				for (ignore in ignoreJsonPathValidation.split(","))
    				{
    					if(data[0]==ignore){continue jsonpath}
    				}			

    				def json_path = data[0]
    				def expected_result = data[1]
    				def actual_result

    				//Write validation results into Result spreadsheet
    				sheet_v.addCell(new Label(0,validation_row_num,test_step))
    				sheet_v.addCell(new Label(1,validation_row_num,json_path))
    				sheet_v.addCell(new Label(2,validation_row_num,expected_result))

    				//Retriving the Json value from response using getJsonValue method imported from json-path.jar file
    				try{
    					 actual_result = jsonParse.getJsonValue(json_path)    					 
    				}
    				catch(Exception expObj)
    				{
    					sheet_v.addCell(new Label(4,validation_row_num,"Exception"))
    					sheet_v.addCell(new Label(5,validation_row_num,expObj.toString()))
    					log.info "JSON Validation -> ${test_step} -> ${json_path} has Exception: ${expObj}"
    					continue
    				}

    				sheet_v.addCell(new Label(3,validation_row_num,actual_result))

    				if(expected_result==actual_result)
    				{
    					sheet_v.addCell(new Label(4,validation_row_num,"Pass"))
    					log.info "JSON Validation -> ${test_step} -> ${json_path} -> Pass"    					
    				}
    				else
    				{
    					result="Fail"
    					sheet_v.addCell(new Label(4,validation_row_num,"Fail"))
    					log.info "JSON Validation -> ${test_step} -> ${json_path} -> Fail. Expected_Result:(${expected_result}) but Actual_Result:(${actual_result})"    					
    				}
    				validation_row_num++
    			}			
			sheet_r.addCell(new Label(5,i,result))
			assertionList.add(result)
			log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} is Complete"

		}

			for(int x=0;x<sheet_r.getColumns();x++)
			{	
				CellView cell=sheet_r.getColumnView(x);
				cell.setSize(9000)	
				sheet_r.setColumnView(x,cell);			
			}

			for(int x=0;x<sheet_v.getColumns();x++)
			{	
				CellView cell=sheet_v.getColumnView(x);
				cell.setSize(9000)	
				sheet_v.setColumnView(x,cell);			
			}
			workbook.write()
			workbook.close()

	}	
}

//Groovy Script -----
def path=inputFileName.substring(0, inputFileName.lastIndexOf("\\"));
if(!new File(path).exists())
{	
	log.info "Input File Path \"${path}\" does not exists and hence exiting the program"
	return
}
def fileNameSplit = new File(inputFileName.toString()).getName().split("\\.")
def outputFileName = "${path}\\${fileNameSplit[0]}_Results.${fileNameSplit[1]}"
def driver = new Driver(inputFileName,outputFileName,ignoreJsonPathValidation)
try{
driver.run(log,testRunner,context)
}
catch(Exception expObj)
{
	log.info expObj
}

File file = new File("${path}\\result.txt")
file.write("");
Driver.assertionList.each { 
	file << "$it\n"
}

return
//assertFalse (Driver.assertionList.contains("Fail"))

import com.eviware.soapui.model.testsuite.TestRunner.Status
import groovy.json.JsonSlurper 
import java.util.Date;
import java.io.File;
import java.util.*;
import java.lang.*; 
import jxl.*;
import jxl.write.*
import com.soapui.BuildJsonPath.*
import soapui.com.jayway.jsonpath.*
import com.coverity.recorder.*
import java.util.UUID;


inputFileName =  "C:\\Users\\nskrishnamoorthy\\Desktop\\Coverity\\Demo\\Automation_Template_Groovy_Input.xls"
recorderUrl="http://10.219.250.78:9096"

class Driver
{
	def inputFileName
	def outputFileName
	def recorderUrl

	def Driver(def inputFileName,def outputFileName,def recorderUrl)
	{
		this.inputFileName=inputFileName
		this.outputFileName=outputFileName
		this.recorderUrl=recorderUrl
	}

	def run(def log,def testRunner,def context)
	{
		def wb = Workbook.getWorkbook(new File(inputFileName))
		WritableWorkbook workbook = Workbook.createWorkbook(new File(outputFileName),wb)
		def sheet1 = wb.getSheet(0)
		def sheet_r = workbook.getSheet(0)
		sheet_r.addCell(new Label(5,0,"Result"))
		sheet_r.addCell(new Label(6,0,"Exception"))
		//Create Validation Sheet
		WritableSheet sheet_v = workbook.createSheet("JSON_Validation",1)
		sheet_v.addCell(new Label(0,0,"Test Step"))
		sheet_v.addCell(new Label(1,0,"JSON Path"))
		sheet_v.addCell(new Label(2,0,"Expected Result"))
		sheet_v.addCell(new Label(3,0,"Actual Result"))
		sheet_v.addCell(new Label(4,0,"Result"))
		sheet_v.addCell(new Label(5,0,"Exception"))
		def row_count = sheet1.getRows()
		def inputPath = new File(inputFileName).getParent().toString()
		def validation_row_num=1
		for(def i=1; i< row_count; i++)
		{
			def test_suite = sheet1.getCell(0,i).getContents()
			if(test_suite.isEmpty()){continue}
			def test_case = sheet1.getCell(1,i).getContents()
			def test_step = sheet1.getCell(2,i).getContents()
			def validationCsvFilePath = "${inputPath}\\${sheet1.getCell(3,i).getContents()}"
			def run_state = sheet1.getCell(4,i).getContents()
			def result = "Pass"
			if (run_state.equalsIgnoreCase("FALSE"))
			{
				continue
			}

			def t_suite = testRunner.testCase.testSuite.project.testSuites[test_suite]
			if (t_suite==null)
			{
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} -> Test Suite is not present"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,"Test Suite is not present"))
				continue	
			}
			def tc = t_suite.testCases[test_case]

			if(tc==null)
			{
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} -> Test Case is not present"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,"Test Case is not present"))
				continue	
			}

			def ts = tc.testSteps[test_step]

			if(ts==null)
			{
			log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} -> Test Step is not present"
			sheet_r.addCell(new Label(5,i,"Error"))
			sheet_r.addCell(new Label(6,i,"Test Step is not present"))
			continue	
			}

			//Initialize the Coverity-Recorder URL
			def recorder
			try{
				recorder = new Recorder(recorderUrl);
			}
			catch(Exception expObj) {
				log.info "Recorder is not running.Please error: ${expObj}"				
			}

			//Starting the Coverity recorder
			UUID uuid = UUID.randomUUID();
			recorder.startTest(uuid.toString(),"${test_case}-${test_step}","Test Suite: ${uuid}", true);			
			log.info "Coverity -> recorder is on and test is being recorded"
			log.info "Executing Test Step: ${test_step}"
			def response
			try{
				def runner = ts.run(testRunner, context)
				response= runner.response.contentAsString
				//log.info "Response is ${response}"
			}
			catch(Exception expObj) {
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} has Exception ${expObj}"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,expObj.toString()))
				recorder.stopTest(uuid.toString(),false,"","");
				continue
			}

			recorder.stopTest(uuid.toString(),true,"","");
			log.info "Coverity -> test is recorded and recorder is off"

			if(!new File(validationCsvFilePath).exists() || validationCsvFilePath=="skip")
			{
				log.info "Skipping the validation and validation JSON File: ${new File(validationCsvFilePath).getName()} does not exists."
				continue
			}

			log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} Request is run and JSON Validation in progress"

			//Parsing JSON response using JsonParse class imported from json-path.jar file
			def jsonParse
			try{
			jsonParse = new JsonParse(response)
			}
			catch(Exception expObj)
			{
				log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} has Exception ${expObj}"
				sheet_r.addCell(new Label(5,i,"Error"))
				sheet_r.addCell(new Label(6,i,expObj.toString()))
				continue	
			}

			//Looping through json path and expected result from csv file
			context.fileReader = new BufferedReader(new FileReader(validationCsvFilePath))
			def rowsData = context.fileReader.readLines()
			def rowsize = rowsData.size()
		     for(def j=1; j< rowsize;  j++)
    			{
    				def rowdata = rowsData[j]
    				//Splitting the string to 2 based on comma delimiter overcoming csv restriction
    				String[] data = rowdata.split(",",2)
    				data[0]=data[0].replaceAll("=","")
    				data[0]=data[0].replaceAll("\"","")
    				data[1]=data[1].replaceAll("=","")
    				data[1]=data[1].replaceAll("\"","")
    				def json_path = data[0]
    				def expected_result = data[1]
    				def actual_result

    				//Write validation results into Result spreadsheet
    				sheet_v.addCell(new Label(0,validation_row_num,test_step))
    				sheet_v.addCell(new Label(1,validation_row_num,json_path))
    				sheet_v.addCell(new Label(2,validation_row_num,expected_result))

    				//Retriving the Json value from response using getJsonValue method imported from json-path.jar file
    				try{
    					 actual_result = jsonParse.getJsonValue(json_path)    					 
    				}
    				catch(Exception expObj)
    				{
    					sheet_v.addCell(new Label(4,validation_row_num,"Exception"))
    					sheet_v.addCell(new Label(5,validation_row_num,expObj.toString()))
    					log.info "JSON Validation -> ${test_step} -> ${json_path} has Exception: ${expObj}"
    					continue
    				}

    				sheet_v.addCell(new Label(3,validation_row_num,actual_result))

    				if(expected_result==actual_result)
    				{
    					sheet_v.addCell(new Label(4,validation_row_num,"Pass"))
    					log.info "JSON Validation -> ${test_step} -> ${json_path} -> Pass"
    				}
    				else
    				{
    					result="Fail"
    					sheet_v.addCell(new Label(4,validation_row_num,"Fail"))
    					log.info "JSON Validation -> ${test_step} -> ${json_path} -> Fail. Expected_Result:(${expected_result}) but Actual_Result:(${actual_result})"
    				}
    				validation_row_num++
    			}			
			sheet_r.addCell(new Label(5,i,result))
			log.info "RUN STATUS -> ${test_suite} -> ${test_case} -> ${test_step} -> ${i}/${(row_count-1)} is Complete"

		}

			for(int x=0;x<sheet_r.getColumns();x++)
			{	
				CellView cell=sheet_r.getColumnView(x);
				cell.setSize(9000)	
				sheet_r.setColumnView(x,cell);			
			}

			for(int x=0;x<sheet_v.getColumns();x++)
			{	
				CellView cell=sheet_v.getColumnView(x);
				cell.setSize(9000)	
				sheet_v.setColumnView(x,cell);			
			}
			workbook.write()
			workbook.close()
	}	
}

//Groovy Script -----
def path=inputFileName.substring(0, inputFileName.lastIndexOf("\\"));
if(!new File(path).exists())
{	
	log.info "Input File Path \"${path}\" does not exists and hence exiting the program"
	return
}
def fileNameSplit = new File(inputFileName.toString()).getName().split("\\.")
def outputFileName = "${path}\\${fileNameSplit[0]}_Results.${fileNameSplit[1]}"
def driver = new Driver(inputFileName,outputFileName,recorderUrl)
try{
driver.run(log,testRunner,context)
}
catch(Exception expObj)
{
	log.info expObj
}

